    func tagSearch(sentence: String) -> [String] {
        let tagsArray = sentence.componentsSeparatedByString(" ");
        var result: [String] = [String]();
        for word in tagsArray {
            for (key, tagArray) in emojiDict {
                for tag in tagArray {
                    if tag == word {
                        result.append(key);
                    }
                }
            }
        }
        return result;
    }

